

public class comparisonoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 51;
		int y = 54;
		int z = 27;
		System.out.println(x == y);
		System.out.println(x != y);
		System.out.println(x > y);
		System.out.println(x <= y);
		System.out.println(x < z);
	}

}
