package softwarecoding2;

public class P2 {
	///////////////////////////////////////////////////////////////////////
	// 소프트웨어코딩 2강. 변수
	// 2. 변수 예제2
	// 21. 04. 27. (화)
	// 2125341020 안규원
	///////////////////////////////////////////////////////////////////////
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int year = 0; // 변수 선언 및 초기화
		int age = 14; // 변수 선언 및 초기화
		// year 콘솔출력
		System.out.println(year);
		// age 콘솔 출력
		System.out.println(age);
		
		year = age + 2000; // year 변수에 2000 더함
		age = age + 1; // age 변수에 1 더함
		// year 콘솔출력
		System.out.println(year);
		// age 콘솔출력
		System.out.println(age);

	}

}
