package softwarecoding2;

public class P5 {
	///////////////////////////////////////////////////////////////////////
	// 소프트웨어코딩 2강. 변수
	// 5. SpecialCharEx
	// 21. 04. 27. (화)
	// 2125341020 안규원
	///////////////////////////////////////////////////////////////////////
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println('\''); // 작음따옴표 삽입
		System.out.println("abc\t123\b456"); // 백스페이스로 3을 지움.
		// 허나 지금 이클립스에선 먹지않음..
		System.out.println('\n'); // 줄바꿈
		System.out.println("\"Hello\""); // 큰따옴표 삽입
		System.out.println("c:\\"); // \를 삽입
	}
}
